package main

import "fmt"

// 通过率 100% 速度 2N 88% ， 内存 22%
func canPlaceFlowers(flowerbed []int, n int) bool {
	fn := len(flowerbed)
	if fn < n {
		return false
	}
	i := 0
	for i < fn && flowerbed[i] != 1 {
		i++
	}
	cnt := i / 2
	if i == fn && flowerbed[i-1] == 0 {
		cnt = (i + 1) / 2
	}

	for i = i + 1; i < fn-1; i++ {

		if flowerbed[i-1] == 0 && flowerbed[i] == 0 && flowerbed[i+1] == 0 {
			flowerbed[i] = 1
			cnt++
		}
	}
	if i == fn-1 && flowerbed[i-1] == 0 && flowerbed[i] == 0 {
		flowerbed[i] = 1
		cnt++
	}
	fmt.Println(flowerbed)
	fmt.Println(cnt, n)
	return cnt >= n
}

// 优化， 添加哨兵 速度 100%， 内存 10%
func canPlaceFlowers2(flowerbed []int, n int) bool {
	fn := len(flowerbed)
	nums := make([]int, fn+2)
	copy(nums[1:fn+1], flowerbed)
	for i := 1; i < fn+1; i++ {
		if nums[i-1] == 0 && nums[i] == 0 && nums[i+1] == 0 {
			nums[i] = 1
			n--
		}
	}
	return n <= 0
}

